create sequence hibernate_sequence start with 1 increment by 1;

create table book (id bigint generated by default as identity, author varchar(255) not null, course varchar(255) not null, title varchar(128) not null, primary key (id));
create table book_post (id bigint generated by default as identity, for_sale boolean not null, book_id bigint not null, seller_username varchar(255) not null, primary key (id));
create table message (id bigint generated by default as identity, date binary(255) not null, message varchar(256) not null, read boolean not null, reciever_username varchar(255) not null, sender_username varchar(255) not null, primary key (id));
create table user_roles (user_username varchar(255) not null, roles varchar(255));
create table users (username varchar(32) not null, enabled boolean not null, firstname varchar(62) not null, lastname varchar(62) not null, password varchar(255) not null, primary key (username));
